mapper.select:
select RTYPE, CODE, GRNAME, RPUSER, REDCODE, RDATE from( select rownum as rnum, t.* from ( SELECT RTYPE, CODE, GRNAME, RPUSER, REDCODE, RDATE FROM ( SELECT RTYPE, CODE, GRNAME, RPUSER, REDCODE, RDATE FROM ( SELECT RTYPE, CODE, GRNAME, RPUSER, REDCODE, TO_CHAR(RDATE,'YYYY-MM-DD') AS RDATE FROM VIEW_REPORT_HOST ORDER BY RDATE DESC ) ) where 1=1
AND RPUSER LIKE '%' || #{keyword} || '%'
AND GRNAME LIKE '%' || #{keyword} || '%'
) t where rownum <= #{startList}+#{sizePerPage}-1 ) where rnum >= #{startList}

SELECT SC_NAME
FROM STUDYCAFE
WHERE SC_CODE = #{SCCODE}

SELECT GU_ID AS ID
FROM GUEST 
WHERE GU_CODE = #{GUCODE}

SELECT HO_ID 
FROM HOST
WHERE HO_CODE = #{HOCODE}

SELECT GR_NAME
FROM GROUP_REGISTRATION
