mapper.select:
SELECT COUNT(*) AS COUNT FROM ( SELECT * FROM VIEW_ADMIN_GROUPLIST ) where 1=1
AND GRCODE = #{keyword}
AND GRNAME LIKE '%' || #{keyword} || '%'

select GRCODE, GCDATE, GRNAME, CATEGORYCODE, BRDATE, RUN from( select rownum as rnum, t.* from ( SELECT GRCODE, GCDATE, GRNAME, CATEGORYCODE, BRDATE, RUN FROM ( SELECT * FROM VIEW_ADMIN_GROUPLIST ORDER BY TO_NUMBER(GRCODE) DESC ) where 1=1
AND GRCODE = '1'
AND GRNAME LIKE '%' || '합격' || '%'
) t where rownum <= #{startList}+#{sizePerPage}-1 ) where rnum >= #{startList}

SELECT GU_ID
FROM GUEST 
WHERE GU_CODE = #{leaderCode}

SELECT LF_LIST
FROM LOCATION_FIRST
WHERE LF_CODE = #{LFCODE}

SELECT LS_LIST 
FROM LOCATION_SECOND
WHERE LS_CODE = #{LSCODE}
