mapper.select:
SELECT COUNT(*) AS COUNT FROM HOST where 1=1
AND HO_CODE = #{keyword}
AND HO_ID LIKE '%' || #{keyword} || '%'

select HOCODE, HOID, HOEMAIL, HODATE, HOUN, CK from( select rownum as rnum, t.* from ( SELECT HOCODE, HOID, HOEMAIL, HODATE, HOUN, CK FROM ( SELECT H.HO_CODE AS HOCODE, H.HO_ID AS HOID, HI.HO_EMAIL AS HOEMAIL, TO_CHAR(H.HO_DATE, 'YYYY-MM-DD') AS HODATE, NVL2(HU.HO_UNCODE,'탈퇴','활동중') AS HOUN, NVL(AP.CK, 0) AS CK FROM HOST H JOIN HOST_INFO HI ON H.HO_CODE = HI.HO_CODE LEFT JOIN HOST_UNREG HU ON H.HO_CODE = HU.HO_CODE LEFT JOIN (SELECT HO_CODE, COUNT(AD_PERDATE) AS CK FROM STUDYCAFE WHERE AD_PERDATE IS NOT NULL GROUP BY HO_CODE) AP ON H.HO_CODE = AP.HO_CODE ORDER BY TO_NUMBER(H.HO_CODE) DESC ) where 1=1
AND HOCODE = #{keyword}
AND HOID LIKE '%' || #{keyword} || '%'
) t where rownum <= #{startList}+#{sizePerPage}-1 ) where rnum >= #{startList}

SELECT SC_CODE AS SCCODE, SC_NAME AS SCNAME, SC_ADDR1  AS SCADDR1, SC_TEL AS SCTEL
FROM STUDYCAFE
WHERE HO_CODE=#{HOCODE}
